import requests
from lxml import html
login_url = 'https://qldt.ptit.edu.vn/default.aspx?page=dangnhap'
loading = {
    '__EVENTTARGET': 'ctl00$ContentPlaceHolder1$ctl00$ddlTuan',
    '__EVENTARGUMENT': '',
    '__LASTFOCUS': '',
    '__VIEWSTATE': '',
    '__VIEWSTATEGENERATOR': 'CA0B0334',
    'ctl00$ContentPlaceHolder1$ctl00$ddlChonNHHK': '20221',
    'ctl00$ContentPlaceHolder1$ctl00$ddlLoai': '0',
    'ctl00$ContentPlaceHolder1$ctl00$ddlTuan': ''
}
dd = { 'TKBT1' : 'Tuần 01 [Từ 15/08/2022 -- Đến 21/08/2022]', 'TKBT2' : 'Tuần 02 [Từ 22/08/2022 -- Đến 28/08/2022]', 'TKBT3' : 'Tuần 03 [Từ 29/08/2022 -- Đến 04/09/2022]',
      'TKBT4' : 'Tuần 04 [Từ 05/09/2022 -- Đến 11/09/2022]', 'TKBT5' : 'Tuần 05 [Từ 12/09/2022 -- Đến 18/09/2022]', 'TKBT6' : 'Tuần 06 Từ 19/09/2022 -- Đến 25/09/2022]',
      'TKBT7' : 'Tuần 07 [Từ 26/09/2022 -- Đến 02/10/2022]', 'TKBT8' : 'Tuần 08 [Từ 03/09/2022 -- Đến 09/10/2022]', 'TKBT9' : 'Tuần 09 [Từ 10/10/2022 -- Đến 16/10/2022]',
      'TKBT10' : 'Tuần 10 [Từ 17/10/2022 -- Đến 23/10/2022]', 'TKBT11' : 'Tuần 11 [Từ 24/10/2022 -- Đến 30/10/2022]', 'TKBT12' : 'Tuần 12 [Từ 31/10/2022 -- Đến 06/11/2022]',
      'TKBT13' : 'Tuần 13 [Từ 07/11/2022 -- Đến 13/11/2022]', 'TKBT14' : 'Tuần 14 [Từ 14/11/2022 -- Đến 20/11/2022]', 'TKBT15' : 'Tuần 15 [Từ 21/11/2022 -- Đến 27/11/2022]',
      'TKBT16' : 'Tuần 16 [Từ 28/11/2022 -- Đến 04/12/2022]'}

def show(pay, arg):
    with requests.Session() as s:
        ans = ""
        schedule = list()
        response = s.post(login_url, data=pay)
        loading['ctl00$ContentPlaceHolder1$ctl00$ddlTuan'] = dd[arg]
        res = s.post("https://qldt.ptit.edu.vn/default.aspx?page=thoikhoabieu", data=loading)
        tree = html.fromstring(res.content.decode(res.encoding))
        l = tree.xpath('//td//@onmouseover')
        d = {'Thứ Hai': 2, 'Thứ Ba': 3, 'Thứ Tư': 4, 'Thứ Năm': 5, 'Thứ Sáu': 6, 'Thứ Bảy': 7, 'Chủ Nhật': 8}
        # schedule = list()
        for item in l:
            tmp = item.split("'")
            schedule.append(tuple([d[tmp[7]], int(tmp[13]), tmp[7], tmp[3], tmp[5], tmp[11], tmp[17]]))

        schedule.sort()
        ans += 'Thứ' + ' | ' + 'Tiết BD' + ' | ' + 'Tên Môn học' + ' | ' + 'Mã môn - Nhóm HP' + ' | ' + 'Phòng' + ' | ' + 'Giảng Viên' + '\n'
        ans += '----------------------------------------------------------------------------------------------'
        for item in schedule:
            ans = ans + item[2] + ' | ' + str(item[1]) + ' | ' + item[3] + ' | ' + item[4] + ' | ' + item[5] + ' | ' + item[6] + '\n'
            ans += '----------------------------------------------------------------------------------------------'

        if len(schedule) != 0: return ans
        return "-1"